mapper.select:
SELECT COUNT(DISTINCT article.id)
FROM tb_article article
LEFT JOIN tb_article_tag article_tag
ON article.id = article_tag.article_id

    article.is_delete = #{conditionVO.isDelete}

        AND article.article_title LIKE CONCAT('%', #{conditionVO.keywords}, '%')


        AND article.category_id = #{conditionVO.categoryId}


        AND article.status = #{conditionVO.status}


        AND article.type = #{conditionVO.type}


        AND article_tag.tag_id = #{conditionVO.tagId}

SELECT info.id , info.article_cover, info.article_title, info.create_time, category.category_name, tag.id AS tag_id, tag.tag_name, info.type, info.is_top, info.is_delete, info.`status` FROM (SELECT id, article_cover, article_title, create_time, type, is_top, is_delete,`status`, category_id FROM tb_article
is_delete = #{conditionVO.isDelete}
AND article_title LIKE CONCAT('%', #{conditionVO.keywords}, '%')
AND category_id = #{conditionVO.categoryId}
AND `status` = #{conditionVO.status}
AND type = #{conditionVO.type}
AND id IN (SELECT article_id FROM tb_article_tag WHERE tag_id = #{conditionVO.tagId})
ORDER BY is_top DESC, id DESC LIMIT #{offset}, #{size}) info
LEFT JOIN tb_category category ON info.category_id = category.id LEFT JOIN tb_article_tag article_tag ON info.id = article_tag.article_id LEFT JOIN tb_tag tag ON tag.id = article_tag.tag_id /*ORDER BY info.is_top DESC, info.id desc*/
