mapper.select:
SELECT COUNT(*)
FROM tb_comment `comment`
LEFT JOIN tb_user_info user_info ON `comment`.user_id = user_info.id


        `comment`.type =#{conditionVO.type}


        `comment`.is_review = #{conditionVO.isReview}


        user_info.nickname LIKE CONCAT('%', #{conditionVO.keywords}, '%')

SELECT `comment`.id, user_info.avatar, user_info.nickname, reply_info.id reply_nickname,article.article_title, `comment`.comment_content, `comment`.type, `comment`.is_review, `comment`.create_time FROM tb_comment `comment` LEFT JOIN tb_article article ON `comment`.topic_id = article.id LEFT JOIN tb_user_info user_info ON `comment`.user_id = user_info.id LEFT JOIN tb_user_info reply_info ON `comment`.reply_user_id = reply_info.id
`comment`.type =#{conditionVO.type}
`comment`.is_review = #{conditionVO.isReview}
user_info.nickname LIKE CONCAT('%', #{conditionVO.keywords}, '%')
ORDER BY `comment`.id DESC LIMIT #{offset}, #{size}

SELECT topic_id, COUNT(*) FROM tb_comment WHERE topic_id IN
#{talkId}
AND parent_id IS NULL GROUP BY topic_id
