mapper.select:
SELECT COUNT(DISTINCT article.id)
FROM tb_article article
LEFT JOIN tb_article_tag article_tag
ON article.id = article_tag.article_id

    article.is_delete = #{articleSearchVO.isDelete}

        AND article.article_title LIKE CONCAT('%', #{articleSearchVO.keywords}, '%')


        AND article.category_id = #{articleSearchVO.categoryId}


        AND article.status = #{articleSearchVO.status}


        AND article.type = #{articleSearchVO.type}


        AND article_tag.tag_id = #{articleSearchVO.tagId}

SELECT article.id , article.article_cover, article.article_title, article.create_time, category.category_name, tag.id AS tag_id, tag.tag_name, article.type, article.is_top, article.is_delete, article.`status` FROM (SELECT id, article_cover, article_title, create_time, type, is_top, is_delete,`status`, category_id FROM tb_article
is_delete = #{articleSearchVO.isDelete}
AND article_title LIKE CONCAT('%', #{articleSearchVO.keywords}, '%')
AND category_id = #{articleSearchVO.categoryId}
AND `status` = #{articleSearchVO.status}
AND type = #{articleSearchVO.type}
AND id IN (SELECT article_id FROM tb_article_tag WHERE tag_id = #{articleSearchVO.tagId})
ORDER BY is_top DESC, id DESC LIMIT #{offset}, #{size}) article
LEFT JOIN tb_category category ON article.category_id = category.id LEFT JOIN tb_article_tag article_tag ON article.id = article_tag.article_id LEFT JOIN tb_tag tag ON tag.id = article_tag.tag_id
